import React, { useState } from 'react'; // Importer useState depuis React

import WheelComponent from "./WheelConstructor.jsx";

export default function RoulettePage({ segments, onBack }) {
  const [winner, setWinner] = useState(null); // Définir l'état winner et la fonction setWinner

  // Fonction pour alterner entre les couleurs violet et rose
  const alternateColors = (index) => {
    return index % 2 === 0 ? "#C622C0" : "#dc87d9";
  };

  // Générer les couleurs des segments en fonction de l'alternance définie
  const segColors = segments.map((segment, index) => alternateColors(index));

  const onFinished = (winner) => {
    setWinner(winner); // Utiliser la fonction setWinner pour mettre à jour l'état winner
  };

  return (
    <div>
      <h1 className='title marg-top2'>SPIN !</h1>
      <div className='wheel marg-top2'>
        
        <WheelComponent
          segments={segments}
          segColors={segColors}
          onFinished={onFinished}
          primaryColor="black"
          contrastColor="white"
          buttonText="Spin"
          isOnlyOnce={false}
          size={160}
          upDuration={500}
          downDuration={600}
          fontFamily="'Luckiest Guy', cursive"
          fontSize={400}
        />
      </div>
      <div className='flex-center'>
        <button>{winner}</button>
      </div>

      <button className='button-stay button-pink' onClick={onBack}>Modify Wheel</button>
    </div>
  );
}
