import React, { useState } from 'react';
import classNames from 'classnames';
import './css/wheel.css';

const App = () => {
  const [selectedNumber, setSelectedNumber] = useState(null);
  const [spinning, setSpinning] = useState(false);
  const [customResult, setCustomResult] = useState('');

  const spinWheel = () => {
    setSpinning(true);
    const random = Math.floor(Math.random() * 37);
    setTimeout(() => {
      setSelectedNumber(random);
      setSpinning(false);
    }, 3000); // Simulate wheel spinning for 3 seconds
  };

  const handleCustomResultChange = (event) => {
    setCustomResult(event.target.value);
  };

  const setCustomResultNumber = () => {
    const result = parseInt(customResult, 10);
    if (!isNaN(result) && result >= 0 && result <= 36) {
      setSelectedNumber(result);
    } else {
      alert('Veuillez saisir un nombre entre 0 et 36.');
    }
  };

  return (
    <div className="App">
      <h1>Roulette Game</h1>
      <div className={classNames('wheel', { spinning })} onClick={!spinning ? spinWheel : null}>
        {Array.from({ length: 37 }, (_, index) => (
          <div
            key={index}
            className={classNames('number', { selected: selectedNumber === index })}
          >
            {index}
          </div>
        ))}
      </div>
      <div className="custom-input">
        <input
          type="number"
          placeholder="Entrez un résultat personnalisé (0-36)"
          value={customResult}
          onChange={handleCustomResultChange}
        />
        <button onClick={setCustomResultNumber}>Définir le résultat personnalisé</button>
      </div>
      <div className="result">
        {spinning ? 'Spinning...' : selectedNumber !== null && `Résultat : ${selectedNumber}`}
      </div>
    </div>
  );
};

export default App;
