import React, { useState } from 'react';
import './css/wheel.css';

const Wheel = () => {
  const [numFields, setNumFields] = useState(2);
  const [result, setResult] = useState(null);

  const handleAddField = () => {
    setNumFields(prevNum => prevNum + 1);
  };

  const handleStartSpin = () => {
    // Logique pour tourner la roue et afficher le résultat
    const randomResult = Math.floor(Math.random() * numFields);
    setResult(randomResult);
  };

  return (
    <div className="wheel-container">
      <h1>Wheel Spinner</h1>

      <div className="fields-container">
        {[...Array(numFields)].map((_, index) => (
          <div key={index} className="field"></div>
        ))}
      </div>

      <button onClick={handleAddField}>Ajouter un champ</button>
      <button onClick={handleStartSpin} disabled={numFields < 2}>Lancer la roue</button>
      
      {result !== null && (
        <p>Résultat: Champ {result + 1}</p>
      )}
    </div>
  );
};

export default Wheel;
