import React, { useState, useRef, useEffect } from 'react';
import './css/wheel.css';

const pointer = (
  <line x1="50" y1="10" x2="50" y2="0" stroke="red" strokeWidth="2" />
);

const RouletteGame = () => {
  const [fields, setFields] = useState([]);
  const [rotation, setRotation] = useState(0);
  const [pointerAngle, setPointerAngle] = useState(0);
  const [isSpinning, setIsSpinning] = useState(false);
  const [result, setResult] = useState(null);

  const wheelRef = useRef(null);
  const pointerRef = useRef(null);

  useEffect(() => {
    // Initialize the roulette sections
    initializeSections();
  }, []);

  const initializeSections = () => {
    const numberOfSections = 8; // Change this to the desired number of sections

    const newFields = Array.from({ length: numberOfSections }, (_, index) => ({
      id: index + 1,
      name: `Section ${index + 1}`,
      color: getRandomColor(),
    }));

    setFields(newFields);
  };

  const getRandomColor = () => {
    return `#${Math.floor(Math.random() * 16777215).toString(16)}`;
  };

  const calculatePath = (index) => {
    const totalFields = fields.length;
    const angle = (360 / totalFields) * index;
    const startAngle = angle;
    const endAngle = angle + (360 / totalFields);

    const radius = 50;

    const startX = 50 + radius * Math.cos((startAngle * Math.PI) / 180);
    const startY = 50 + radius * Math.sin((startAngle * Math.PI) / 180);
    const endX = 50 + radius * Math.cos((endAngle * Math.PI) / 180);
    const endY = 50 + radius * Math.sin((endAngle * Math.PI) / 180);

    const largeArcFlag = endAngle - startAngle <= 180 ? 0 : 1;
    const color = fields[index].color;

    return (
      <path
        key={index}
        d={`M50 50 L${startX} ${startY} A${radius} ${radius} 0 ${largeArcFlag} 1 ${endX} ${endY} Z`}
        fill={color}
        stroke="black"
        strokeWidth="1"
      />
    );
  };

  const handleStartSpin = () => {
    setResult(null);
    setIsSpinning(true);

    const totalDuration = 3000;
    const start = Date.now();

    const spinAnimation = () => {
      const elapsed = Date.now() - start;
      const progress = Math.min(elapsed / totalDuration, 1);
      const easedProgress = 0.5 - 0.5 * Math.cos(progress * Math.PI);

      const rotation = 1080 * easedProgress;
      setRotation(rotation);
    };

    const spinInterval = setInterval(spinAnimation, 16);

    setTimeout(() => {
      clearInterval(spinInterval);
      setIsSpinning(false);

      // Determine the result after spinning stops
      detectSectionOnRotation();
    }, totalDuration + 100);
  };

  const detectSectionOnRotation = () => {
    const wheelRect = wheelRef.current.getBoundingClientRect();

    const wheelCenterX = wheelRect.left + wheelRect.width / 2;
    const wheelCenterY = wheelRect.top + wheelRect.height / 2;

    const angle = Math.atan2(wheelCenterY, wheelCenterX) * (180 / Math.PI);
    const normalizedAngle = (angle + 360) % 360;

    const sectionAngle = 360 / fields.length;
    const selectedSectionIndex = Math.floor(normalizedAngle / sectionAngle);

    setResult(selectedSectionIndex !== null ? selectedSectionIndex : 0);
  };

  return (
    <div className="roulette-container">
      <h2>Roulette Spin Wheel</h2>
      <div className="roulette" ref={wheelRef}>
        <svg viewBox="0 0 100 100" width="200" height="200">
          <g transform={`rotate(${rotation} 50 50)`}>
            {fields.map((_, index) => calculatePath(index))}
          </g>
          <g transform={`rotate(${pointerAngle} 50 50)`} ref={pointerRef}>
            {pointer}
          </g>
        </svg>
      </div>

      <button onClick={handleStartSpin} disabled={isSpinning}>
        Lancer la roue
      </button>
      {result !== null && <p>RÃ©sultat: {fields[result].name}</p>}
    </div>
  );
};

export default RouletteGame;
