import React, { useState, useRef } from 'react';
import './css/wheel.css';

const triangle = (
  <polygon
    points="50,5 55,-5 45,-5"
    fill="yellow"
    stroke="black"
    strokeWidth="1"
  />
);

const RouletteWheel = () => {
  const [fields, setFields] = useState([]);
  const [result, setResult] = useState(null);
  const [rotation, setRotation] = useState(0);
  const [isSpinning, setIsSpinning] = useState(false);

  const wheelRef = useRef(null);
  const triangleRef = useRef(null);

  const handleAddField = () => {
    const newColor = generateRandomColor();
    const newNumber = generateRandomNumber();
    const newField = { id: fields.length, number: newNumber, color: newColor };
    setFields((prevFields) => [...prevFields, newField]);
  };

  const handleRemoveField = () => {
    if (fields.length > 1) {
      setFields((prevFields) => prevFields.slice(0, -1));
    }
  };

  const handleFieldNameChange = (id, value) => {
    setFields((prevFields) =>
      prevFields.map((field) => (field.id === id ? { ...field, number: value } : field))
    );
  };

  const handleStartSpin = () => {
    setResult(null);
    setIsSpinning(true);

    const totalDuration = 3000;
    const start = Date.now();

    const spinAnimation = () => {
      const elapsed = Date.now() - start;
      const progress = Math.min(elapsed / totalDuration, 1);
      const easedProgress = 0.5 - 0.5 * Math.cos(progress * Math.PI);

      const rotation = 1080 * easedProgress;
      setRotation(rotation);
    };

    const spinInterval = setInterval(spinAnimation, 16);

    setTimeout(() => {
      clearInterval(spinInterval);
      setIsSpinning(false);

      const wheelRect = wheelRef.current.getBoundingClientRect();
      const triangleRect = triangleRef.current.getBoundingClientRect();

      const triangleCenterX = triangleRect.left + triangleRect.width / 2;
      const triangleCenterY = triangleRect.top + triangleRect.height / 2;

      const wheelCenterX = wheelRect.left + wheelRect.width / 2;
      const wheelCenterY = wheelRect.top + wheelRect.height / 2;

      const dx = triangleCenterX - wheelCenterX;
      const dy = triangleCenterY - wheelCenterY;

      let angle = Math.atan2(dy, dx) * (180 / Math.PI);
      angle = (angle + 360) % 360;

      const sectionAngle = 360 / fields.length;
      let selectedSectionIndex = Math.floor(angle / sectionAngle);

      // Adjust the index to avoid potential off-by-one errors
      selectedSectionIndex = (selectedSectionIndex + fields.length) % fields.length;

      setResult(selectedSectionIndex);
    }, totalDuration + 100);
  };

  const calculatePath = (index) => {
    const totalFields = fields.length;
    const angle = (360 / totalFields) * index;
    const startAngle = angle;
    const endAngle = angle + (360 / totalFields);

    const radius = 50;

    const startX = 50 + radius * Math.cos((startAngle * Math.PI) / 180);
    const startY = 50 + radius * Math.sin((startAngle * Math.PI) / 180);
    const endX = 50 + radius * Math.cos((endAngle * Math.PI) / 180);
    const endY = 50 + radius * Math.sin((endAngle * Math.PI) / 180);

    const largeArcFlag = endAngle - startAngle <= 180 ? 0 : 1;
    const color = generateRandomColor();

    const textX = 50 + radius * 0.8 * Math.cos(((startAngle + endAngle) / 2) * (Math.PI / 180));
    const textY = 50 + radius * 0.8 * Math.sin(((startAngle + endAngle) / 2) * (Math.PI / 180));

    return {
      path: (
        <path
          key={index}
          d={`M50 50 L${startX} ${startY} A${radius} ${radius} 0 ${largeArcFlag} 1 ${endX} ${endY} Z`}
          fill={color}
          stroke="black"
          strokeWidth="1"
        />
      ),
      text: (
        <text x={textX} y={textY} fontSize="5" textAnchor="middle" fill="black">
          {fields[index] ? fields[index].number : ''}
        </text>
      ),
    };
  };

  const generateRandomColor = () => {
    const letters = '0123456789ABCDEF';
    let color = '#';
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  };

  const generateRandomNumber = () => {
    return Math.floor(Math.random() * 100); // Adjust the range as needed
  };

  return (
    <div className="wheel-container">
      <h2>Roulette Wheel</h2>
      <div className="wheel" ref={wheelRef}>
        <svg viewBox="0 0 100 100" width="200" height="200">
          <g transform={`rotate(${rotation} 50 50)`}>
            {fields.map((_, index) => {
              const { path, text } = calculatePath(index);
              const isHighlighted = !isSpinning && result !== null && index === result;

              return (
                <g
                  key={index}
                  className={isHighlighted ? 'highlighted-section selected-indicator' : 'selected-indicator'}
                >
                  {path}
                  {text}
                </g>
              );
            })}
          </g>
          <g ref={triangleRef}>{triangle}</g>
        </svg>
      </div>

      <div className="fields-container">
        {fields.map((field) => (
          <input
            key={field.id}
            type="text"
            placeholder={`Champ ${field.id}`}
            value={field.number}
            onChange={(e) => handleFieldNameChange(field.id, e.target.value)}
          />
        ))}
      </div>
      <button onClick={handleAddField}>Ajouter un champ</button>
      <button onClick={handleRemoveField} disabled={fields.length < 2}>
        Supprimer un champ
      </button>
      <button onClick={handleStartSpin} disabled={fields.length < 2 || isSpinning}>
        Lancer la roue
      </button>
      {result !== null && <p>RÃ©sultat: {fields[result] ? fields[result].number : ''}</p>}
    </div>
  );
};

export default RouletteWheel;
