import React, { useState, useRef, useEffect } from 'react';
import { useInView } from 'react-intersection-observer';
import './css/wheel.css';

const triangle = (
  <polygon
    points="50,5 55,15 45,15"
    fill="yellow"
    stroke="black"
    strokeWidth="1"
  />
);

const Wheel = () => {
  const [fields, setFields] = useState([
    { id: 1, name: 'Section 1', color: 'green' },
  ]);
  const [result, setResult] = useState(null);
  const [selectedSectionIndex, setSelectedSectionIndex] = useState(null);
  const [wheelRotation, setWheelRotation] = useState('');

  const triangleRef = useRef(null);

  const handleAddField = () => {
    const newColor = fields.length % 2 === 0 ? 'lightgreen' : 'lightblue';
    const newField = { id: fields.length + 1, name: `Section ${fields.length + 1}`, color: newColor };
    setFields((prevFields) => [...prevFields, newField]);
  };

  const handleRemoveField = () => {
    if (fields.length > 1) {
      setFields((prevFields) => prevFields.slice(0, -1));
    }
  };

  const handleFieldNameChange = (id, value) => {
    setFields((prevFields) =>
      prevFields.map((field) => (field.id === id ? { ...field, name: value } : field))
    );
  };

  const calculateRotation = (elapsedTime) => {
    const easing = (t) => t * (2 - t);
    const normalizedTime = elapsedTime / 2000; // totalDuration is fixed at 2000
    const rotatedAngle = 1200 * easing(normalizedTime);
    return `rotate(${rotatedAngle} 50 50)`;
  };

  const [triangleEntry] = useInView({
    triggerOnce: true,
    rootMargin: '0px 0px -50% 0px', // Adjust rootMargin as needed
  });

  const trianglePos = useRef({ x: 0, y: 0 });

  useEffect(() => {
    if (triangleEntry) {
      const rect = triangleRef.current.getBoundingClientRect();
      trianglePos.current = {
        x: rect.left + rect.width / 2,
        y: rect.top + rect.height,
      };
    }
  }, [triangleEntry]);

  useEffect(() => {
    if (fields.length > 0) {
      console.log(`Nombre de sections dans la roulette : ${fields.length}`);
    }
  }, [fields]);

  const handleStartSpin = () => {
    const initialDelay = 100;
    const refreshInterval = 16;
    setResult(null);

    let startTime = null;

    const spinAnimation = () => {
      const currentTime = Date.now();
      const elapsedTime = currentTime - startTime;

      if (elapsedTime < 2000) {
        const rotation = calculateRotation(elapsedTime);
        setWheelRotation(rotation);

        const sectionCenters = fields.map((_, index) => {
          const angle = (index + 0.5) * (360 / fields.length);
          const x = 50 + 30 * Math.cos((angle * Math.PI) / 180);
          const y = 50 + 30 * Math.sin((angle * Math.PI) / 180);
          return { x, y };
        });

        const closestSection = sectionCenters.reduce((acc, curr) =>
          Math.abs(curr.y - trianglePos.current.y) < Math.abs(acc.y - trianglePos.current.y)
            ? curr
            : acc
        );

        const resultIndex = sectionCenters.findIndex(
          (center) => center.x === closestSection.x && center.y === closestSection.y
        );

        if (resultIndex !== -1) {
          setSelectedSectionIndex(resultIndex);
        }
      } else {
        clearInterval(animationInterval);
        const spinResult = selectedSectionIndex !== null ? selectedSectionIndex : 0;
        setResult(spinResult);
        setSelectedSectionIndex(spinResult);
      }
    };

    startTime = Date.now();
    setWheelRotation(calculateRotation(0));
    const animationInterval = setInterval(spinAnimation, refreshInterval);
    setTimeout(() => clearInterval(animationInterval), 2000 + initialDelay);
  };

  const calculatePath = (index) => {
    const totalFields = fields.length;
    const angle = 360 / totalFields;
    const startAngle = index * angle;
    const endAngle = startAngle + angle;
    const radius = 50;

    const startX = 50 + radius * Math.cos((startAngle * Math.PI) / 180);
    const startY = 50 + radius * Math.sin((startAngle * Math.PI) / 180);
    const endX = 50 + radius * Math.cos((endAngle * Math.PI) / 180);
    const endY = 50 + radius * Math.sin((endAngle * Math.PI) / 180);

    const largeArcFlag = angle > 180 ? 1 : 0;
    const color = fields[index].color;

    const textX = 50 + radius * 0.8 * Math.cos(((startAngle + endAngle) / 2) * (Math.PI / 180));
    const textY = 50 + radius * 0.8 * Math.sin(((startAngle + endAngle) / 2) * (Math.PI / 180));

    return {
      path: (
        <path
          key={index}
          d={`M50 50 L${startX} ${startY} A${radius} ${radius} 0 ${largeArcFlag} 1 ${endX} ${endY} Z`}
          fill={color}
          stroke="black"
          strokeWidth="1"
        />
      ),
      text: (
        <text x={textX} y={textY} fontSize="5" textAnchor="middle" fill="black">
          {fields[index].name}
        </text>
      ),
    };
  };

  return (
    <div className="roulette-container">
      <h2>Wheel Spinner</h2>
      <div className="wheel">
        <svg viewBox="0 0 100 100" width="200" height="200">
          <g transform={wheelRotation}>
            {fields.map((field, index) => {
              const { path, text } = calculatePath(index);
              const isHighlighted = selectedSectionIndex !== null && index === selectedSectionIndex;

              return (
                <g
                  key={index}
                  className={isHighlighted ? 'highlighted-section selected-indicator' : 'selected-indicator'}
                >
                  {path}
                  {text}
                </g>
              );
            })}
          </g>
          <g ref={triangleRef} {...triangleEntry}>
            {triangle}
          </g>
        </svg>
      </div>

      <div className="fields-container">
        {fields.map((field) => (
          <input
            key={field.id}
            type="text"
            placeholder={`Champ ${field.id}`}
            value={field.name}
            onChange={(e) => handleFieldNameChange(field.id, e.target.value)}
          />
        ))}
      </div>
      <button onClick={handleAddField}>Ajouter un champ</button>
      <button onClick={handleRemoveField} disabled={fields.length < 2}>
        Supprimer un champ
      </button>
      <button onClick={handleStartSpin} disabled={fields.length < 2}>
        Lancer la roue
      </button>
      {result !== null && <p>Résultat: {fields[result].name}</p>}
    </div>
  );
};

export default Wheel;
