import React, { useState, useRef } from 'react';
import './css/wheel.css';

function App() {
  const [numSections, setNumSections] = useState(4); // Nombre de sections par défaut
  const [sections, setSections] = useState(Array.from({ length: numSections }, (_, index) => `Section ${index + 1}`));
  const [winnerIndex, setWinnerIndex] = useState(null);
  const rouletteRef = useRef(null);
  const pointerRef = useRef(null);

  const handleNumSectionsChange = (e) => {
    const newNumSections = parseInt(e.target.value, 10);
    setNumSections(newNumSections);
    setSections(Array.from({ length: newNumSections }, (_, index) => `Section ${index + 1}`));
  };

  const setManualWinner = () => {
    const pointerAngle = 360 - (360 / numSections) * winnerIndex;
    pointerRef.current.style.transform = `rotate(${pointerAngle}deg)`;
  };

  const spinRoulette = () => {
    const randomWinnerIndex = Math.floor(Math.random() * numSections);
    setWinnerIndex(randomWinnerIndex);

    // Calcul de l'angle pour pointer vers la section gagnante
    const angle = (360 / numSections) * randomWinnerIndex;
    rouletteRef.current.style.transition = 'transform 3s ease-out';
    rouletteRef.current.style.transform = `rotate(${3600 + angle}deg)`; // On fait tourner la roulette 10 tours
  };

  return (
    <div className="App">
      <label>
        Nombre de sections :
        <input type="number" value={numSections} onChange={handleNumSectionsChange} min="1" />
      </label>
      <button onClick={spinRoulette}>Lancer la roulette</button>

      <div className="roulette-container">
        <div className="roulette" ref={rouletteRef}>
          {sections.map((section, index) => (
            <div key={index} className={`section ${winnerIndex === index ? 'winner' : ''}`}>
              {section}
            </div>
          ))}
          <div className="pointer" ref={pointerRef} onClick={setManualWinner}></div>
        </div>
      </div>
    </div>
  );
}

export default App;
