import React from 'react';

import "./css/home.css";
import "./css/button.css";
import "./css/reset.css";

export default function HomePage({ onSelectPage }) {
  return (
  
    <div>
        <div>
            <h1 className='title marg-top'>
                <span>
                    SUPER
                </span>
                <span>
                    SPIN
                </span>
                <span>
                    WHEEL
                </span>
            </h1>
        </div>
        <div className='flex-column marg-top'>
            <button onClick={() => onSelectPage('input')}>PLAY</button>
            <button onClick={() => onSelectPage('roulette')}>CUSTOM</button>
        </div>
    </div>
  );
}
