import React, { useState } from 'react';
import './css/wheel.css';

function App() {
  const [numSections, setNumSections] = useState(1);

  const generateRoulette = () => {
    const angle = 360 / numSections;
    const sections = [];
  
    for (let i = 0; i < numSections; i++) {
      const clipPath = i === numSections - 1
        ? `polygon(50% 50%, 100% 0, 100% ${100 / numSections}%)`
        : `polygon(50% 50%, 100% 0, 100% 100%)`;
  
      sections.push(
        <div
          key={i}
          className="section"
          style={{
            background: getRandomColor(),
            transform: `rotate(${i * angle}deg)`,
            clipPath: clipPath
          }}
        ></div>
      );
    }
  
    return sections;
  };
  

  const getRandomColor = () => {
    const letters = '0123456789ABCDEF';
    let color = '#';
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  };

  return (
    <div className="App">
      <h1>Roulette Game</h1>
      <label htmlFor="numSections">Nombre de sections:</label>
      <input
        type="number"
        id="numSections"
        min="1"
        max="30"
        value={numSections}
        onChange={(e) => setNumSections(parseInt(e.target.value))}
      />
      <button onClick={generateRoulette}>Générer Roulette</button>
      <div className="roulette">{generateRoulette()}</div>
    </div>
  );
}

export default App;
