import React from 'react';
import WheelComponent from 'react-wheel-of-prizes';
import './styles.css'; // Importez le fichier CSS contenant les styles personnalisés

export default function CustomWheelComponent({ segments, segColors, onFinished }) {
  // Déterminez si le texte des segments doit être affiché avec une taille personnalisée
  const useCustomFontSize = true; // Changez cette valeur selon vos besoins

  return (
    <div>
      <h1>Roulette</h1>
      <div>
        <WheelComponent
          segments={segments}
          segColors={segColors}
          onFinished={onFinished}
          primaryColor="black"
          contrastColor="white"
          buttonText="Spin"
          isOnlyOnce={false}
          size={190}
          upDuration={500}
          downDuration={600}
          fontFamily="Arial"
          // Conditionnellement ajouter la classe pour la taille du texte personnalisée
          className={useCustomFontSize ? 'segment-text' : ''}
        />
      </div>
      {/* Bouton pour retourner à la page de configuration des champs */}
      <button onClick={onBack}>Modify Roulette</button>
    </div>
  );
}
