// SpinWheel.jsx

import React, { useState } from 'react';
import './css/wheel.css';

import Needle from './Needle'; // Import the Needle component

const Wheel = () => {
  const [fields, setFields] = useState([{ id: 1, name: '', color: 'lightblue' }]);
  const [result, setResult] = useState(null);
  const [selectedSection, setSelectedSection] = useState(null);
  const [wheelRotation, setWheelRotation] = useState('');
  const [needlePosition, setNeedlePosition] = useState(0);

  const handleNeedleClick = () => {
    const sectionIndex = calculatePointedSection();
    setResult(sectionIndex);
    setSelectedSection(sectionIndex);
  };

  const calculatePointedSection = () => {
    const totalFields = fields.length;
    const angle = 360 / totalFields;
    const pointedAngle = (360 + (needlePosition * 360)) % 360;
    const pointedSection = Math.floor(pointedAngle / angle);
    return pointedSection;
  };

  // Ajoutez cette fonction pour calculer la position de l'aiguille dans calculatePath
  const calculateNeedlePosition = (angle) => {
    const radius = 50;
    const needleLength = 10; // Longueur de l'aiguille
    const needleX = 50 + (radius - needleLength / 2) * Math.cos((angle * Math.PI) / 180);
    const needleY = 50 + (radius - needleLength / 2) * Math.sin((angle * Math.PI) / 180);
    return { x: needleX, y: needleY };
  };

  // Modifiez calculatePath pour inclure la ligne de l'aiguille
  const calculatePath = (index, needleAngle) => {
    const totalFields = fields.length;
    const angle = 360 / totalFields;
    const startAngle = index * angle;
    const endAngle = startAngle + angle;
    const radius = 50;

    const startX = 50 + radius * Math.cos((startAngle * Math.PI) / 180);
    const startY = 50 + radius * Math.sin((startAngle * Math.PI) / 180);
    const endX = 50 + radius * Math.cos((endAngle * Math.PI) / 180);
    const endY = 50 + radius * Math.sin((endAngle * Math.PI) / 180);

    const largeArcFlag = angle > 180 ? 1 : 0;
    const color = index % 2 === 0 ? 'blue' : 'red';

    const textX = 50 + radius * 0.8 * Math.cos(((startAngle + endAngle) / 2) * (Math.PI / 180));
    const textY = 50 + radius * 0.8 * Math.sin(((startAngle + endAngle) / 2) * (Math.PI / 180));

    // Calcule la position de l'aiguille pour la section actuelle
    const needlePosition = calculateNeedlePosition(needleAngle);

    return {
      path: (
        <g key={index} className="wheel-section">
          <path
            d={`M50 50 L${startX} ${startY} A${radius} ${radius} 0 ${largeArcFlag} 1 ${endX} ${endY} Z`}
            fill={color}
            stroke="black"
            strokeWidth="1"
          />
          {/* Ajoutez la ligne représentant l'aiguille */}
          <line
            x1={50}
            y1={50}
            x2={needlePosition.x}
            y2={needlePosition.y}
            stroke="black"
            strokeWidth="2"
            strokeLinecap="round"
            strokeDasharray="5, 5" // Cette propriété crée une ligne pointillée
          />
        </g>
      ),
      text: (
        <text x={textX} y={textY} fontSize="5" textAnchor="middle" fill="black">
          {fields[index].name}
        </text>
      ),
    };
  };

  // Modifiez le rendu de la roue pour inclure la ligne de l'aiguille
  return (
    <div className="roulette-container">
      <h2>Wheel Spinner</h2>
      <div className="wheel">
        <svg viewBox="0 0 100 100" width="200" height="200">
          <g transform={wheelRotation}>
            {fields.map((field, index) => {
              const { path, text } = calculatePath(index, needlePosition);
              const isHighlighted = selectedSection !== null && index === selectedSection;

              return (
                <g key={index} className={isHighlighted ? 'highlighted-section' : ''}>
                  {path}
                  {text}
                </g>
              );
            })}
            {selectedSection !== null && (
              <line
                x1="50"
                y1="5"
                x2="50"
                y2="25"
                stroke="black"
                strokeWidth="2"
                strokeLinecap="round"
              />
            )}
          </g>
        </svg>
        <Needle needlePosition={needlePosition} onClick={handleNeedleClick} />
      </div>
      <div className="fields-container">
        {fields.map((field) => (
          <input
            key={field.id}
            type="text"
            placeholder={`Champ ${field.id}`}
            value={field.name}
            onChange={(e) => handleFieldNameChange(field.id, e.target.value)}
          />
        ))}
      </div>
      <button onClick={handleAddField}>Ajouter un champ</button>
      <button onClick={handleRemoveField} disabled={fields.length < 2}>
        Supprimer un champ
      </button>
      <button onClick={handleStartSpin} disabled={fields.length < 2}>
        Lancer la roue
      </button>
      {result !== null && <p>Résultat: {fields[result].name}</p>}
    </div>
  );
};

export default Wheel;
