import React, { useState, useEffect } from 'react';

export default function SettingsPage({ onBack, onToggleMusic, onToggleSound }) {
  const [musicEnabled, setMusicEnabled] = useState(true);
  const [soundEnabled, setSoundEnabled] = useState(true);

  useEffect(() => {
    // Fonction pour mettre à jour l'état initial des cases à cocher
    const updateCheckBoxes = () => {
      const storedMusicPreference = localStorage.getItem('musicEnabled');
      if (storedMusicPreference !== null) {
        setMusicEnabled(JSON.parse(storedMusicPreference));
      }

      const storedSoundPreference = localStorage.getItem('soundEnabled');
      if (storedSoundPreference !== null) {
        setSoundEnabled(JSON.parse(storedSoundPreference));
      }
    };

    // Appeler la fonction pour mettre à jour les cases à cocher lors du montage du composant
    updateCheckBoxes();
  }, []);

  // Mettre à jour localStorage lorsqu'il y a des changements dans les préférences
  useEffect(() => {
    localStorage.setItem('musicEnabled', JSON.stringify(musicEnabled));
    onToggleMusic(musicEnabled);
  }, [musicEnabled, onToggleMusic]);

  useEffect(() => {
    localStorage.setItem('soundEnabled', JSON.stringify(soundEnabled));
    onToggleSound(soundEnabled);
  }, [soundEnabled, onToggleSound]);

  return (
    <div>
      <h2>Paramètres</h2>
      <label>
        <input
          type="checkbox"
          checked={musicEnabled}
          onChange={() => setMusicEnabled(!musicEnabled)}
        />
        Activer la musique de fond
      </label>
      <br />
      <label>
        <input
          type="checkbox"
          checked={soundEnabled}
          onChange={() => setSoundEnabled(!soundEnabled)}
        />
        Activer le son de clic
      </label>
      <br />
      <button onClick={onBack}>Retour</button>
    </div>
  );
}
