import React, { useState, useEffect } from 'react';
import InputPage from './components/ConfigurePage';
import RoulettePage from './components/RoulettePage';
import HomePage from './components/HomePage';

// Importez le fichier audio de clic et de musique de fond
import clickSound from './components/audio/click.mp3';
import backgroundMusic from './components/audio/backSound.mp3';

export default function App() {
  const [currentPage, setCurrentPage] = useState('menu');
  const [segments, setSegments] = useState([]);

  // Créez une fonction pour jouer le son de clic
  const playClickSound = () => {
    const audio = new Audio(clickSound);
    audio.play();
  };

  // Créez une fonction pour jouer la musique de fond en boucle
  const playBackgroundMusic = () => {
    const audio = new Audio(backgroundMusic);
    audio.loop = true;
    audio.play();
  };

  useEffect(() => {
    // Jouez la musique de fond une fois que l'application est montée
    playBackgroundMusic();

    // Retourne une fonction de nettoyage pour arrêter la musique lors du démontage du composant
    return () => {
      const audio = new Audio(backgroundMusic);
      audio.pause();
      audio.currentTime = 0;
    };
  }, []); // Utilisez un tableau vide pour s'assurer que cela ne se déclenche qu'une seule fois

  const handleInputSubmit = (fields) => {
    setSegments(fields);
    setCurrentPage('roulette');
    playClickSound();
  };

  const handleSelectPage = (page) => {
    setCurrentPage(page);
    playClickSound();
  };

  return (
    <div className="App">
      {currentPage === 'menu' && (
        <HomePage onSelectPage={handleSelectPage} />
      )}
      {currentPage === 'input' && (
        <InputPage onSubmit={handleInputSubmit} segments={segments} />
      )}
      {currentPage === 'roulette' && (
        <RoulettePage segments={segments} onBack={() => setCurrentPage('input')} />
      )}
    </div>
  );
}
