import React, { useState, useEffect } from 'react';
import "./css/home.css";
import "./css/button.css";
import "./css/reset.css";

export default function InputPage({ onSubmit }) {
  const [fields, setFields] = useState(['', '']);

  useEffect(() => {
    // Vérifier si le nombre de champs est inférieur à 2, alors ajoutez des champs
    if (fields.length < 2) {
      setFields(['', '']);
    }
  }, [fields]);

  const handleChange = (index, value) => {
    const newFields = [...fields];
    newFields[index] = value;
    setFields(newFields);
  };

  const handleAddField = () => {
    setFields([...fields, '']);
  };

  const handleRemoveField = (index) => {
    // Empêcher la suppression si le nombre de champs est inférieur ou égal à 2
    if (fields.length <= 2) return;
    
    const newFields = [...fields];
    newFields.splice(index, 1);
    setFields(newFields);
  };

  const handleSubmit = () => {
    onSubmit(fields.filter(field => field.trim() !== ''));
  };

  const allFieldsFilled = fields.every(field => field.trim() !== '');

  return (
    <div>
      <div className='marg-top2'>
        <h1 className='second-title'>
          <span>
            CREATE YOUR
          </span>
          <span>
             WHEEL
          </span>
        </h1>
      </div>

      <div className='flex-center marg-top2 marg-bottom'>
        {fields.map((field, index) => (
          <div key={index}>
            <input className='input-player'
              type="text"
              placeholder={`Field ${index + 1}`}
              value={field || ''}
              onChange={e => handleChange(index, e.target.value)}
            />
            {/* Empêcher la suppression si le nombre de champs est inférieur ou égal à 2 */}
            {fields.length > 2 && <button className='button-circle' onClick={() => handleRemoveField(index)}>x</button>}
          </div>
        ))}
        <button className='button-circle' onClick={handleAddField}>+</button>

      </div>
      {/* Empêcher de soumettre s'il n'y a qu'un seul champ */}
      <button className='button-stay' onClick={handleSubmit} disabled={!allFieldsFilled}>PLAY</button>
    </div>
  );
}
