import React, { useState } from 'react';
import InputPage from './components/ConfigurePage';
import RoulettePage from './components/RoulettePage';
import HomePage from './components/HomePage';
import Sound from 'react-sound';
import clickSound from './components/audio/click.mp3';

export default function App() {
  const [currentPage, setCurrentPage] = useState('menu');
  const [segments, setSegments] = useState([]);
  const [isPlaying, setIsPlaying] = useState(false);

  const handleInputSubmit = (fields) => {
    setSegments(fields);
    setCurrentPage('roulette');
    setIsPlaying(true); // Démarrer la lecture du son lorsque la page change
  };

  const handleSelectPage = (page) => {
    setCurrentPage(page);
  };

  return (
    <div className="App">
      <HomePage onSelectPage={handleSelectPage} />
      <InputPage
        visible={currentPage === 'input'}
        onSubmit={handleInputSubmit}
        segments={segments}
      />
      <RoulettePage
        visible={currentPage === 'roulette'}
        segments={segments}
        onBack={() => setCurrentPage('input')}
      />

      <Sound
        url={clickSound}
        playStatus={isPlaying ? Sound.status.PLAYING : Sound.status.STOPPED}
        volume={50}
        onFinishedPlaying={() => setIsPlaying(false)} // Arrête la lecture du son une fois qu'elle est terminée
      />
    </div>
  );
}
