import React, { useState } from 'react';
import './css/wheel.css';

const Wheel = () => {
  const [fields, setFields] = useState([{ id: 1, name: '', color: 'lightblue' }]);
  const [result, setResult] = useState(null);

  const handleAddField = () => {
    const newColor = fields.length % 2 === 0 ? 'lightgreen' : 'lightblue';
    const newField = { id: fields.length + 1, name: '', color: newColor };
    setFields((prevFields) => [...prevFields, newField]);
  };

  const handleRemoveField = () => {
    if (fields.length > 1) {
      setFields((prevFields) => prevFields.slice(0, -1));
    }
  };

  const handleFieldNameChange = (id, value) => {
    setFields((prevFields) =>
      prevFields.map((field) => (field.id === id ? { ...field, name: value } : field))
    );
  };

  const handleStartSpin = () => {
    const randomResult = Math.floor(Math.random() * fields.length);
    setResult(randomResult);
  };
  const calculateRotation = (index) => {
    const totalFields = fields.length;
    const angle = (360 / totalFields) * index;
    const correction = totalFields % 2 === 0 ? 360 / (2 * totalFields) : 0; // Correction pour les nombres pairs de sections
    return `rotate(${angle + correction} 50 50)`;
  };
  

  const calculatePath = (index) => {
    const totalFields = fields.length;
    const angle = (360 / totalFields);
    const startAngle = index * angle;
    const endAngle = startAngle + angle;
    const radius = 50;

    const largeArcFlag = angle > 180 ? 1 : 0;

    return (
      <path
        key={index}
        d={`M50 50 L${50 + radius * Math.cos((startAngle * Math.PI) / 180)} ${50 + radius * Math.sin((startAngle * Math.PI) / 180)} A${radius} ${radius} 0 ${largeArcFlag} 1 ${50 + radius * Math.cos((endAngle * Math.PI) / 180)} ${50 + radius * Math.sin((endAngle * Math.PI) / 180)} Z`}
        fill="red"
      />
    );
  };

  return (
    <div className="roulette-container">
      <h2>Wheel Spinner</h2>
      <div className="wheel">
        <svg viewBox="0 0 100 100" width="200" height="200">
          {fields.map((field, index) => (
            <g key={field.id} transform={calculateRotation(index)}>
              {calculatePath(index)}
              <text x="50" y="60" fontSize="5" textAnchor="middle" fill="black">
                {field.name}
              </text>
            </g>
          ))}
        </svg>
      </div>
      <div className="fields-container">
        {fields.map((field) => (
          <input
            key={field.id}
            type="text"
            placeholder={`Champ ${field.id}`}
            value={field.name}
            onChange={(e) => handleFieldNameChange(field.id, e.target.value)}
          />
        ))}
      </div>
      <button onClick={handleAddField}>Ajouter un champ</button>
      <button onClick={handleRemoveField} disabled={fields.length < 2}>
        Supprimer un champ
      </button>
      <button onClick={handleStartSpin} disabled={fields.length < 2}>
        Lancer la roue
      </button>
      {result !== null && <p>RÃ©sultat: {fields[result].name}</p>}
    </div>
  );
};

export default Wheel;
