import Sound from 'react-sound';
import React, { useState } from 'react';
import clickSound from '..../public/audio/click.mp3';

import "./css/home.css";
import "./css/button.css";
import "./css/reset.css";

export default function HomePage({ onSelectPage }) {

    const [isPlaying, setIsPlaying] = useState(false);

    const handleClick = (page) => {
        setIsPlaying(true);
        setTimeout(() => setIsPlaying(false), 1000); // Stopper le son après 1 seconde (ajustez selon vos besoins)
        onSelectPage(page); // Appel de votre fonction onSelectPage avec le paramètre de la page
    };

  return (
  
    <div>
        <div>
            <h1 className='title marg-top3'>
                <span>
                    SUPER
                </span>
                <span>
                    SPIN
                </span>
                <span>
                    WHEEL
                </span>
            </h1>
        </div>
        <div className='flex-column marg-top'>
            <button onClick={() => handleClick('input')}>PLAY</button>
            <button onClick={() => handleClick('roulette')}>CUSTOM</button>
            <Sound
                url={clickSound}
                playStatus={isPlaying ? Sound.status.PLAYING : Sound.status.STOPPED}
                volume={50} // Ajustez le volume selon vos besoins
            />
        </div>
    </div>
  );
}
