// RoulettePage.jsx
import React from "react";
import WheelComponent from "react-wheel-of-prizes";

export default function RoulettePage(props) {
  const { segments } = props.location.state;

  const segColors = ["#EE4040", "#F0CF50", "#815CD1", "#3DA5E0", "#34A24F"];

  const onFinished = (winner) => {
    console.log(winner);
  };

  return (
    <div>
      <h1>Roulette</h1>
      <div>
        <WheelComponent
          segments={segments}
          segColors={segColors}
          onFinished={(winner) => onFinished(winner)}
          primaryColor="black"
          contrastColor="white"
          buttonText="Spin"
          isOnlyOnce={false}
          size={190}
          upDuration={500}
          downDuration={600}
          fontFamily="Arial"
        />
      </div>
    </div>
  );
}
