import React, { useState } from 'react';
import classNames from 'classnames';
import './css/wheel.css';

const numbers = [...Array(37).keys()];

const App = () => {
  const [selectedNumber, setSelectedNumber] = useState(null);
  const [spinning, setSpinning] = useState(false);

  const spinWheel = () => {
    setSpinning(true);
    const random = Math.floor(Math.random() * 37);
    setTimeout(() => {
      setSelectedNumber(random);
      setSpinning(false);
    }, 3000); // Simulate wheel spinning for 3 seconds
  };

  return (
    <div className="App">
      <h1>Roulette Game</h1>
      <div className={classNames('wheel', { spinning })} onClick={!spinning ? spinWheel : null}>
        {numbers.map((num) => (
          <div
            key={num}
            className={classNames('number', { selected: selectedNumber === num })}
          >
            {num}
          </div>
        ))}
      </div>
      <div className="result">
        {spinning ? 'Spinning...' : selectedNumber !== null && `Result: ${selectedNumber}`}
      </div>
    </div>
  );
};

export default App;
