import { useState } from "react";
import "./css/wheel.css";
import WheelComponent from "react-wheel-of-prizes";

export default function App() {
  const [segments, setSegments] = useState([
    "better luck next time",
    "won 70",
    "won 10",
    "better luck next time",
    "won 2",
    "won uber pass"
  ]);
  const [newSegment, setNewSegment] = useState("");

  const segColors = ["#EE4040", "#F0CF50", "#815CD1", "#3DA5E0", "#34A24F"];

  const addSegment = () => {
    if (newSegment.trim() !== "") {
      const newSegments = [...segments, newSegment];
      setSegments(newSegments);
      setNewSegment("");
    }
  };

  const removeSegment = (indexToRemove) => {
    const newSegments = segments.filter((_, index) => index !== indexToRemove);
    setSegments(newSegments);
  };

  const onFinished = (winner) => {
    console.log(winner);
  };

  return (
    <div className="App">
      <h1>Hello CodeSandbox</h1>
      <div>
        <WheelComponent
          segments={segments} // Utilisation de la liste dynamique segments
          segColors={segColors}
          onFinished={(winner) => onFinished(winner)}
          primaryColor="black"
          contrastColor="white"
          buttonText="Spin"
          isOnlyOnce={false}
          size={190}
          upDuration={500}
          downDuration={600}
          fontFamily="Arial"
        />
        <div>
          <input
            type="text"
            value={newSegment}
            onChange={(e) => setNewSegment(e.target.value)}
          />
          <button onClick={addSegment}>Add Segment</button>
        </div>
        <ul>
          {segments.map((segment, index) => (
            <li key={index}>
              {segment}{" "}
              <button onClick={() => removeSegment(index)}>Remove</button>
            </li>
          ))}
        </ul>
      </div>
      <h2>Start editing to see some magic happen!</h2>
    </div>
  );
}
