import React, { useState } from 'react';
import classNames from 'classnames';
import './App.css';

const App = () => {
  const [results, setResults] = useState(['', '']);
  const [selectedResult, setSelectedResult] = useState(null);
  const [spinning, setSpinning] = useState(false);
  const [customResult, setCustomResult] = useState('');

  const spinWheel = () => {
    setSpinning(true);
    const randomIndex = Math.floor(Math.random() * results.length);
    setTimeout(() => {
      setSelectedResult(results[randomIndex]);
      setSpinning(false);
    }, 3000); // Simulate wheel spinning for 3 seconds
  };

  const handleCustomResultChange = (event) => {
    setCustomResult(event.target.value);
  };

  const setCustomResultText = () => {
    if (customResult.trim() !== '') {
      setResults([...results, customResult]);
      setCustomResult('');
    } else {
      alert('Veuillez saisir du texte.');
    }
  };

  const calculateRotation = () => {
    const angle = 360 / results.length;
    const rotation = Math.floor(Math.random() * results.length) * angle;
    return `rotate(${rotation}deg)`;
  };

  return (
    <div className="App">
      <h1>Roulette Game</h1>
      <div className={classNames('roulette', { spinning })} style={{ transform: spinning ? calculateRotation() : 'none' }}>
        {results.map((result, index) => (
          <div
            key={index}
            className={classNames('segment', { selected: selectedResult === result })}
          >
            {result}
          </div>
        ))}
        <div className="needle"></div>
      </div>
      <div className="custom-input">
        <input
          type="text"
          placeholder="Entrez un résultat personnalisé"
          value={customResult}
          onChange={handleCustomResultChange}
        />
        <button onClick={setCustomResultText}>Ajouter un résultat personnalisé</button>
      </div>
      <div className="result">
        {spinning ? 'Spinning...' : selectedResult !== null && `Résultat : ${selectedResult}`}
      </div>
      <button onClick={!spinning ? spinWheel : null} disabled={spinning}>
        Lancer la roulette
      </button>
    </div>
  );
};

export default App;
