import React, { useState, useRef } from 'react';
import { System, Box, Vector } from 'detect-collisions';
import './css/wheel.css';

const App = () => {
  const [results, setResults] = useState(['', '']);
  const [selectedResult, setSelectedResult] = useState(null);
  const [spinning, setSpinning] = useState(false);
  const [customResult, setCustomResult] = useState('');
  const collisionSystem = new System();

  const wheelRef = useRef(null);
  const triangleRef = useRef(null);

  const handleCustomResultChange = (event) => {
    setCustomResult(event.target.value);
  };

  const setCustomResultText = () => {
    if (customResult.trim() !== '') {
      setResults([...results, customResult]);
      setCustomResult('');
    } else {
      alert('Veuillez saisir du texte.');
    }
  };

  const handleStartSpin = () => {
    setResults(['', '']); // Réinitialisez les résultats au début de chaque tour
    setSelectedResult(null);
    setSpinning(true);

    const totalDuration = 3000;
    const start = Date.now();
    const samples = 200;

    const spinAnimation = () => {
      const elapsed = Date.now() - start;
      const progress = Math.min(elapsed / totalDuration, 1);
      const easedProgress = 0.5 - 0.5 * Math.cos(progress * Math.PI);

      const rotation = 1080 * easedProgress;
      wheelRef.current.style.transform = `rotate(${rotation}deg)`;
    };

    const spinInterval = setInterval(spinAnimation, 16);

    setTimeout(() => {
      clearInterval(spinInterval);
      setSpinning(false);

      const triangleRect = triangleRef.current?.getBoundingClientRect();
      const wheelRect = wheelRef.current?.getBoundingClientRect();

      if (!triangleRect || !wheelRect) {
        // Si l'un des deux est null, il est possible que la référence ne soit pas encore définie.
        // Vous pouvez ignorer la suite du code ou gérer l'erreur selon vos besoins.
        console.error("Erreur: Les références ne sont pas définies.");
        return;
      }

const triangleCenterX = triangleRect.left + triangleRect.width / 2;
const triangleCenterY = triangleRect.top + triangleRect.height / 2;

const wheelCenterX = wheelRect.left + wheelRect.width / 2;
const wheelCenterY = wheelRect.top + wheelRect.height / 2;


      const deltaX = triangleCenterX - wheelCenterX;
      const deltaY = triangleCenterY - wheelCenterY;

      let angle = (Math.atan2(deltaY, deltaX) * 180) / Math.PI;
      angle = (angle + 360) % 360;

      const sectionAngle = 360 / results.length;
      const selectedSectionIndex = Math.floor(angle / sectionAngle);

      setSelectedResult(selectedSectionIndex !== null ? selectedSectionIndex : 0);

      // Créez un cercle pour représenter la position du triangle
      const trianglePosition = new Vector(triangleCenterX, triangleCenterY);
      const triangleRadius = 10; // Ajustez la taille du rayon selon vos besoins

      // Utilisez la fonction de raycasting pour détecter les collisions avec chaque section
      const hit = collisionSystem.raycast(trianglePosition, trianglePosition);

      if (hit) {
        const { body } = hit;
        console.log('Collision détectée avec la section : ', body.userData);
        // Faites quelque chose en réponse à la collision ici
      }
    }, totalDuration + 100);
  };

  // Créez et insérez chaque section dans le système de collision
  results.forEach((_, index) => {
    const widthDeLaSection = 50; // Ajustez la largeur de chaque section selon vos besoins
    const heightDeLaSection = 50; // Ajustez la hauteur de chaque section selon vos besoins
    const wheelCenterX = wheelRef.current.getBoundingClientRect().left + wheelRef.current.getBoundingClientRect().width / 2;
    const wheelCenterY = wheelRef.current.getBoundingClientRect().top + wheelRef.current.getBoundingClientRect().height / 2;

    const section = collisionSystem.createBox(
      new Vector(wheelCenterX, wheelCenterY),
      widthDeLaSection,
      heightDeLaSection
    );
    section.userData = { sectionIndex: index }; // userData peut être utilisé pour stocker des informations supplémentaires
    collisionSystem.insert(section);
  });

  return (
    <div className="App">
      <h1>Roulette Game</h1>
      <div className={`roulette ${spinning ? 'spinning' : ''}`} ref={wheelRef}>
        {results.map((result, index) => (
          <div key={index} className={`segment ${selectedResult === index ? 'selected' : ''}`}>
            {result}
          </div>
        ))}
        <div className="needle" ref={triangleRef}></div>
      </div>
      <div className="custom-result-container">
        <input
          type="text"
          placeholder="Nouveau résultat"
          value={customResult}
          onChange={handleCustomResultChange}
        />
        <button onClick={setCustomResultText}>Ajouter</button>
      </div>
      <button onClick={handleStartSpin} disabled={spinning}>
        Lancer la roulette
      </button>
    </div>
  );
};

export default App;
