import React from 'react';
import WheelComponent from 'react-wheel-of-prizes';

export default function RoulettePage({ segments, onBack }) {
  // Fonction pour alterner entre les couleurs violet et rose
  const alternateColors = (index) => {
    return index % 2 === 0 ? "#C622C0" : "#e47ce1";
  };

  // Générer les couleurs des segments en fonction de l'alternance définie
  const segColors = segments.map((segment, index) => alternateColors(index));

  const onFinished = (winner) => {
    console.log(winner);
  };

  return (
    <div>
      <h1>Roulette</h1>
      <div>
        <WheelComponent
          segments={segments}
          segColors={segColors}
          onFinished={onFinished}
          primaryColor="black"
          contrastColor="white"
          buttonText="Spin"
          isOnlyOnce={false}
          size={190}
          upDuration={500}
          downDuration={600}
          fontFamily="Arial"
        />
      </div>
      {/* Bouton pour retourner à la page de configuration des champs */}
      <button onClick={onBack}>Modify Roulette</button>
    </div>
  );
}
