import Sound from 'react-sound';
import React, { useState } from 'react';
import clickSound from './audio/click.mp3';

import "./css/home.css";
import "./css/button.css";
import "./css/reset.css";

export default function HomePage({ onSelectPage }) {

    const [isPlaying, setIsPlaying] = useState(false);

    const handleClick = (page) => {
        setIsPlaying(true);
    };

    const handleFinishedPlaying = (page) => {
        onSelectPage(page); // Déclenche le changement de page une fois que le son est terminé
        setIsPlaying(false); // Arrête la lecture du son
    };

  return (
  
    <div>
        <div>
            <h1 className='title marg-top3'>
                <span>
                    SUPER
                </span>
                <span>
                    SPIN
                </span>
                <span>
                    WHEEL
                </span>
            </h1>
        </div>
        <div className='flex-column marg-top'>
            <button onClick={() => handleClick('input')}>PLAY</button>
            <button onClick={() => handleClick('roulette')}>CUSTOM</button>
            <Sound
                url={clickSound}
                playStatus={isPlaying ? Sound.status.PLAYING : Sound.status.STOPPED}
                volume={50}
                onFinishedPlaying={() => handleFinishedPlaying('input')} // Utilisez handleFinishedPlaying avec le nom de la page
            />
        </div>
    </div>
  );
}
