// src/App.js
import React, { useState } from 'react';
import './css/wheel.css';

const Roulette = ({ numSections, onSpin }) => {
  const [rotationAngle, setRotationAngle] = useState(0);

  const spinRoulette = () => {
    const randomAngle = Math.floor(Math.random() * 360);
    setRotationAngle(randomAngle);
    onSpin(randomAngle);
  };

  return (
    <div className="roulette-container">
      <div className="roulette" style={{ transform: `rotate(${rotationAngle}deg)` }}>
        {[...Array(numSections)].map((_, index) => (
          <div key={index} className="roulette-section" style={{ transform: `rotate(${index * (360 / numSections)}deg)` }}>
            {/* Ajoutez le style et le contenu de chaque section ici */}
          </div>
        ))}
      </div>
      <button onClick={spinRoulette}>Tourner la roulette</button>
    </div>
  );
};

const Cursor = ({ angle }) => (
  <div className="cursor" style={{ transform: `rotate(${angle}deg)` }}>
    {/* Style et contenu du curseur ici */}
  </div>
);

function App() {
  const numSections = 8; // Vous pouvez ajuster le nombre de sections selon vos besoins
  const [cursorAngle, setCursorAngle] = useState(0);

  const handleSpin = (angle) => {
    setCursorAngle(angle);
  };

  return (
    <div className="App">
      <Roulette numSections={numSections} onSpin={handleSpin} />
      <Cursor angle={cursorAngle} />
    </div>
  );
}

export default App;
